def doc_to_text_02(doc):
    q_doc_text = doc["question"] + "\n"
    if doc["context"] and doc["context"] != "":
        q_doc_text += doc["context"] + "\n"
    choices = ",".join(
        [
            f"{idx}.{choice}"
            for idx, choice in zip(doc["choices"]["id"], doc["choices"]["text"])
        ]
    )
    return f"質問:{q_doc_text}" f"選択肢:{choices}\n" "回答:"

